func (xds *XxxDemoSelect) sqlScan(ctx context.Context, v interface{}) error {
	rows := &sql.Rows{}
	query, args := xds.sqlQuery().Query()
	if err := xds.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (xds *XxxDemoSelect) sqlQuery() sql.Querier {
	selector := xds.sql
	selector.Select(selector.Columns(xds.fields...)...)
	return selector
}
